@prefix object: <http://comindware.com/ontology/object#>.
@prefix math: <http://comindware.com/logics/math#>.
@prefix cmw: <http://comindware.com/logics#>.
@prefix assert: <http://comindware.com/logics/assert#>.

{
    ("Project" "DesigningPhase") object:findProperty ?projectPropertyInDesigning.
	("Project" "RealizationPhase" )object:findProperty ?projectPropertyInRealization.
 	("Project" "TestProductionPhase" )object:findProperty ?projectPropertyInTestProduction.
	("Workitem" "DesigningPhase") object:findProperty ?designingPhaseProperty.
	("Workitem" "RealizationPhase" )object:findProperty ?realizationPhaseProperty.
	("Workitem" "TestProductionPhase" )object:findProperty ?testProductionPhaseProperty.
	("Workitem" "ParentWorkitem") object:findProperty ?parentProperty.
    ("Workitem" "_lastWriteDate") object:findProperty ?lastWriteDate.

?item ?projectPropertyInDesigning ?phaseKP. 
from
    {
        ?WorkitemTemplate object:alias "Workitem".
        ?WorkitemItems cmw:container ?WorkitemTemplate.
        (?WorkitemItems ?parentProperty) object:getRootByParentProperty ?rootTask.
       	?rootTask ?designingPhaseProperty ?phaseKP.
  		?WorkitemItems ?lastWriteDate ?lastWriteDateVal.
    } select ?lastWriteDateVal -> ?lastWriteDateList.

?lastWriteDateList math:max ?m.
?m -> ?value.
}
