$values.ConditionExpression:
@prefix cmw: <http://comindware.com/logics#>.
@prefix object: <http://comindware.com/ontology/object#>.
@prefix assert: <http://comindware.com/logics/assert#>.
@prefix math: <http://www.w3.org/2000/10/swap/math#>.
{
                ("ProjectTeam" "WorkPlan") object:findProperty ?WorkPlan.
                ("ProjectTeam" "Performers") object:findProperty ?FIO.
                ("ProjectTeam" "_lastWriteDate") object:findProperty ?lastWriteDate.
                
                ?item ?WorkPlan ?WorkPlanVal.
                ?item ?FIO ?FIOVal.
                ?item ?lastWriteDate ?lastWriteDateVal.
                {
        ?workplanTemplate object:alias "ProjectTeam".
        ?workplan cmw:container ?workplanTemplate.
                                ?workplan ?WorkPlan ?WorkPlanVal.
                                ?workplan ?FIO ?FIOVal.
                                ?workplan ?lastWriteDate ?lastWriteDateVal2.
                                ?lastWriteDateVal math:greaterThan ?lastWriteDateVal2.
                } assert:count ?c.
                if {?c==0}
                then {true -> ?value.}
                else {false -> ?value.}.
}

